mapper.select:
SELECT COUNT(*) FROM likes 
WHERE li_id = #{poNum} 
  AND li_mb_num = #{mbNum} 
  AND li_name = 'FREE'

mapper.delete:
DELETE FROM likes 
WHERE li_id = #{poNum} 
  AND li_mb_num = #{mbNum} 
  AND li_name = 'FREE'

