mapper.select:
SELECT mb_num, mb_uid, mb_nickname, mb_pw, mb_email, mb_rol, mb_score, mb_photo_ver, mb_agree FROM member
WHERE mb_uid = #{id}
  AND mb_email = #{email}

SELECT mb_num, mb_uid, mb_nickname, mb_pw, mb_email, mb_rol, mb_score, mb_photo_ver, mb_agree FROM member
WHERE mb_email = #{email}
LIMIT 1

SELECT mb_num, mb_uid, mb_pw, mb_email
FROM member
WHERE mb_uid = #{id}
  AND mb_email = #{email}
LIMIT 1

mapper.update:
UPDATE member
SET mb_pw = #{pw}
WHERE mb_uid = #{id}

UPDATE member
SET mb_rol = #{role}
WHERE mb_uid = #{id}

UPDATE member
SET mb_email = #{email}
WHERE mb_uid = #{id}

UPDATE member
SET mb_nickname = #{nickname}
WHERE mb_uid = #{id}

UPDATE member
SET mb_photo_data = #{photoData}, mb_photo_type = #{photoType}, mb_photo_ver = #{photoVer}
WHERE mb_uid = #{id}

UPDATE member
SET mb_photo_data = NULL, mb_photo_type = NULL, mb_photo_ver = IFNULL(mb_photo_ver, 0) + 1
WHERE mb_uid = #{id}
